// -----------------------------------------------------------------------------
// CAknExGridContainer::NewL()
// Two-phased constructor.
// -----------------------------------------------------------------------------
//

CZMobileTrackerContainer* CZMobileTrackerContainer::NewL(
    const TRect& aRect, CZMobileTrackerView& aView, TInt aCursorRow, TInt aCursorColumn)
    {
    CZMobileTrackerContainer *self = new (ELeave) CZMobileTrackerContainer(
        aView, aCursorRow, aCursorColumn);
    CleanupStack::PushL (self);
    self->ConstructL (aRect);
    CleanupStack::Pop();
    return self;
    }

// ----------------------------------------------------------------------------
// CZMobileTrackerContainer::CZMobileTrackerContainer()
// Default constructor.
// ----------------------------------------------------------------------------
//
CZMobileTrackerContainer::CZMobileTrackerContainer(
    CZMobileTrackerView& aView, TInt aCursorRow, TInt aCursorColumn) :
    iView (aView), iCursorRow (aCursorRow), iCursorColumn (aCursorColumn)
    {
    }

// -----------------------------------------------------------------------------
// CZMobileTrackerContainer::ConstructL()
// Symbian 2nd phase constructor can leave.
// -----------------------------------------------------------------------------
//
void CZMobileTrackerContainer::ConstructL(const TRect& aRect)
    {
    CreateWindowL();
    SetRect(aRect);
	SetExtentToWholeScreen();   //for full screen 
    ActivateL();
    HandleBoardReset();
    }

