// -----------------------------------------------------------------------------
// CZMobileTrackerContainer::Draw()
// Draw function.
// -----------------------------------------------------------------------------
//
void CZMobileTrackerContainer::Draw(const TRect& aRect) const
    {
    CWindowGc& gc = SystemGc();
	gc.SetPenColor(KRgbRed); 
    gc.SetPenStyle (CGraphicsContext::ESolidPen);
	gc.SetBrushColor(KRgbBlue);
    gc.SetBrushStyle (CGraphicsContext::ESolidBrush);
    gc.DrawRect (aRect);

	TBuf<30> tempStr;
	gc.SetPenColor(KRgbRed); 
    gc.SetPenStyle (CGraphicsContext::ESolidPen);

	gc.SetBrushColor( KRgbYellow );
	gc.SetBrushStyle( CGraphicsContext::ESolidBrush );
	gc.UseFont(CEikonEnv::Static()->AnnotationFont());
	CEikonEnv::Static()->ReadResource(tempStr, RS_TEXT1);
	gc.DrawText(tempStr, TRect(25, 20, 145, 60), 25, CGraphicsContext::ECenter);


	gc.SetBrushColor( KRgbYellow );
	gc.SetBrushStyle( CGraphicsContext::ESolidBrush );
	gc.UseFont(CEikonEnv::Static()->AnnotationFont());
	CEikonEnv::Static()->ReadResource(tempStr, RS_TEXT_MENU);
	gc.DrawText(tempStr, TRect(05, 170, 45, 200), 20, CGraphicsContext::ECenter);

	gc.SetBrushColor( KRgbYellow );
	gc.SetBrushStyle( CGraphicsContext::ESolidBrush );
	gc.UseFont(CEikonEnv::Static()->AnnotationFont());
	CEikonEnv::Static()->ReadResource(tempStr, RS_TEXT_EXIT);
	gc.DrawText(tempStr, TRect(130, 170, 170, 200), 20, CGraphicsContext::ECenter);
    }

